package com.ingic.ezhalbatek.ui.adapters;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import com.ingic.ezhalbatek.fragments.abstracts.BaseFragment;import java.util.ArrayList;import java.util.List;public class ViewPagerAdapter extends FragmentStatePagerAdapter {    private final List<BaseFragment> mFragmentList = new ArrayList<>();    public ViewPagerAdapter(FragmentManager manager) {        super(manager);    }    @Override    public Fragment getItem(int position) {        return mFragmentList.get(position);    }    @Override    public int getCount() {        return mFragmentList.size();    }    @Override    public int getItemPosition(Object object) {        return POSITION_UNCHANGED;    }    public void addFragment(BaseFragment fragment) {        mFragmentList.add(fragment);    }    public void clearList() {        mFragmentList.clear();        notifyDataSetChanged();    }    public Fragment getRegisteredFragment(int position) {        return mFragmentList.get(position);    }}